<script setup>

import {useCashierStore} from '@/stores/cashierStore';
import {useCashierStorePersist} from '@/stores/cashierStorePersist';
import {useAuthStore} from '@/stores/auth';
import { useSettingStore } from '@/stores/settingStore';
import { useI18n } from "vue-i18n";
import { useMagicKeys, whenever } from '@vueuse/core'
import useGlobalFunctions from '@/composables/global_functions.js';
import logo_icon from "@/assets/images/logo-icon.png";

const AsyncCalculater = defineAsyncComponent(() =>
  import("@/components/common/Calculator.vue")
);

const { signOut, isAuthorized } = useGlobalFunctions();
const { t } = useI18n();
const cashierStore = useCashierStore();
const cashierStorePersist = useCashierStorePersist();

const authStore = useAuthStore();
const language = localStorage.getItem('language');

const settingStore = useSettingStore();
const logo = computed(() => {
  return settingStore.logo;
});

onMounted(() => {
  getOrderCount();
});

const cashier_settings = computed(() => {
  return cashierStore.settings;
});

cashierStore.getSettings();

const emits = defineEmits(['closeRegister']);



const initialState = {
    // form data   
    calc_open_close : false,
};
const form = reactive({ ...initialState });
// const calc_open_close = reactive(false);

function openCalculater() {
  setTimeout(function () {
    const calculator_main_div = document.querySelector('#collapseCalculator');
    if(calculator_main_div.classList.contains('show')) {
      form.calc_open_close = true;
    }else{
      form.calc_open_close = false;
    }
  }, 500);
}

function closeCalculater() {
  form.calc_open_close = false;
}

function closeRegister() {

  getOrderCount();

  if(total_hold_orders.value > 0){
      Swal.fire({
          icon: 'warning',
          title: t("You must close all hold orders to close the register"),
          confirmButtonText: 'View Hold Orders',
          showCancelButton: true,
      }).then( (result) => {
          if(result.isConfirmed){
              getHoldOrders();
          }
      });
  }else{
    emits('closeRegister');
  }

}

const total_hold_orders = ref(0);

async function getOrderCount() {
  let response = await axios.get('/api/order/order_count')
  total_hold_orders.value = response.data.total_hold_orders;
}

function getHoldOrders() {
  cashierStore.getHoldOrders();
  $('#holdOrdersModal').modal('show');
}

function getSearchOrders() {
  cashierStore.getSearchOrders();
  $('#searchOrdersModal').modal('show');
}

function getSwitchPrice() {
  $('#SwitchMultiplePriceModal').modal('show');
}

function getProfileModal() {
  //cashierStore.getProfileModal();
  $('#ProfileModal').modal('show');
}


function reload() {
  location.reload();
}

const edit_mode = computed(() => {
  return cashierStore.edit_mode;
})

const edit_order = computed(() => {
  return cashierStore.edit_order;
})

async function switchLanguage(language) {
  var form_data = new FormData();
  form_data.append('language', language)
  await axios.post('/api/switch/language', form_data).then((response) => {

    if (response.data.status_code == 200) {
      //toast.success(response.data.msg);
      localStorage.setItem('language', language);
      window.location.reload();

    }

  }).catch((error) => {
    console.log(error)
  });

}

function uc(value) {
  return _.upperCase(value);
}

async function printLastReceipt() {
  await axios.get('/api/print_last_order').then((response) => {

    if (response.data.status_code == 200) {
      if (response.data.data.receipt_link != '') {
        window.open(
            response.data.data.receipt_link,
            '_blank' // <- This is what makes it open in a new window.
        );
      } else {
        toast.warning('No order found!');
      }

    }

  }).catch((error) => {
    console.log(error)
  });
}

async function toggleFullScreen() {
  var elem = document.body;
  // ## The below if statement seems to work better ## if ((document.fullScreenElement && document.fullScreenElement !== null) || (document.msfullscreenElement && document.msfullscreenElement !== null) || (!document.mozFullScreen && !document.webkitIsFullScreen)) {
  if ((document.fullScreenElement !== undefined && document.fullScreenElement === null) || (document.msFullscreenElement !== undefined && document.msFullscreenElement === null) || (document.mozFullScreen !== undefined && !document.mozFullScreen) || (document.webkitIsFullScreen !== undefined && !document.webkitIsFullScreen)) {
    if (elem.requestFullScreen) {
      elem.requestFullScreen();
    } else if (elem.mozRequestFullScreen) {
      elem.mozRequestFullScreen();
    } else if (elem.webkitRequestFullScreen) {
      elem.webkitRequestFullScreen(Element.ALLOW_KEYBOARD_INPUT);
    } else if (elem.msRequestFullscreen) {
      elem.msRequestFullscreen();
    }
  } else {
    if (document.cancelFullScreen) {
      document.cancelFullScreen();
    } else if (document.mozCancelFullScreen) {
      document.mozCancelFullScreen();
    } else if (document.webkitCancelFullScreen) {
      document.webkitCancelFullScreen();
    } else if (document.msExitFullscreen) {
      document.msExitFullscreen();
    }
  }
}

function openBookTableModal() {
  $('#bookTableModal').modal('show');
}

const register = computed(() => {
  return (authStore.loggedInTerminal) ? authStore.loggedInTerminal.active_business_register : '';
});

const terminal = computed(() => {
  return authStore.loggedInTerminal;
});

const user_name = computed(() => {
  return authStore.user.name;
});

const layout = computed(() => {
  return authStore.user.merchant.layout;
});

//KEYBOARD SORTCUTS
const { alt_f, alt_u , alt_l, alt_r, alt_e, alt_b,alt_c } = useMagicKeys({
  passive: false,
  onEventFired(e) {
    if (e.altKey && e.key === 'f' && e.type === 'keydown')
      e.preventDefault();

    if (e.altKey && e.key === 'e' && e.type === 'keydown')
      e.preventDefault();
  },
})
whenever(alt_f, () => getSearchOrders())
whenever(alt_u, () => getHoldOrders())
whenever(alt_l, () => printLastReceipt())
whenever(alt_r, () => closeRegister())
whenever(alt_e, () => router.push('/orders'))
whenever(alt_b, () => openBookTableModal())
whenever(alt_c, () => document.getElementById('calculater_btn').click())

const multiple_price_id = computed(() => {
    return cashierStorePersist.multiple_price_id
});
  
</script>